def regex_AND3(data, words):
    result=0
    p=re.compile(r'\b({0})\b'.format('|'.join(words)), re.IGNORECASE)
    _list = FindAll(p, data, fPRINT=False)
    # _list = FindIter(p, data, fPRINT=False)
    # print(_list)
    for word in words:
        if word in _list:
            result+=1

    fFOUND = result==len(words)
    print('Found:', fFOUND)


